*/
import React, { Component } from "react";
import moment from "moment";
import { Card, Table, Button, Popconfirm } from "antd";
import { keysListApi } from "../../service/keys";
const columns = [
  {
    title: "ID",
    key: "ID",
    width: 80,
    align: "center",
    dataIndex: "ID",
    //render: (txt, record, index) => index + 1,
  },
  {
    title: "Name",
    dataIndex: "Name",
  },
  {
    title: "Owner",
    dataIndex: "OwnerInfo.name",
  },
  {
    title: "Created At",
    dataIndex: "CreatedAt",
    render: (record) => (
      // console.log("createAt", record);
      <span>{moment(record).format("YYYY-MM-DD HH:mm:ss")}</span>
    ),
  },
  {
    title: "Action",
    render: (txt, record, index) => {
      return (
        <div>
          <Popconfirm
            title="Are you sure to delete?"
            onCancel={() => {
              console.log("cancelled");
            }}
            onConfirm={() => {
              console.log("confirmed");
              //此处调用api接口进行相关操作
            }}
          >
            <Button style={{ margin: "0 1rem" }} type="danger" size="small">
              Delete
            </Button>
          </Popconfirm>
        </div>
      );
    },
  },
];
class Keys extends Component {
  constructor(props) {
    super(props);
    this.state = {
      keys: [],
      isLoaded: false,
      total: 0,
    };
  }
  componentDidMount() {
    const _this = this;
    keysListApi()
      .then((res) => {
        console.log("componentDidMount-keys:", res);
        _this.setState({
          keys: res.keys,
          isLoaded: true,
          total: res.total,
        });
      })
      .catch((error) => {
        _this.setState({
          isLoaded: false,
          error: error,
        });
      });
  }
  demo = () => {
    console.log("11");
  };
  render() {
    return (
      <Card
        title={"Key Manage Panel" + "(Total: " + this.state.total + ")"}
        extra={
          <Button type="primary" size="small" onClick={this.demo}>
            Create
          </Button>
        }
      >
        <Table
          rowKey="ID"
          columns={columns}
          bordered
          dataSource={this.state.keys}
        ></Table>
      </Card>
    );
  }
}
export default Keys;
