import {
  INS_LIST_FETCHED,
  INS_LIST_FILTER,
  INS_LIST_IS_FETCHING,
} from "../../constants/actionTypes";
import { insListApi } from "../../service/instances";
export const filterRegList = (keyword) => ({
  type: INS_LIST_FILTER,
  keyword,
});
export const fetchInsList = () => {
  return (dispatch) => {
    dispatch(fetchingInsList(true));
    insListApi().then((res) => {
      console.log("insAction-res", res);
      // let resData = res.data;
      if (res) {
        dispatch(fetchingInsList(false));
        dispatch(fetchInsListSuccess(res.instances, false));
        //   } else {
        //     //   dispatch(fetchingUserList(false));
        //     //   dispatch(fetchUserListFailed(" 获取用户列表失败"));
      }
    });
    //   .catch((e) => dispatch(fetchUserListFailed(e.message)));
  };
};
export const fetchInsListSuccess = (insList, loading) => ({
  type: INS_LIST_FETCHED,
  insList,
  loading,
});
export const fetchingInsList = (loading) => ({
  type: INS_LIST_IS_FETCHING,
  loading,
});
