import { getAll } from "../../utils/auth";
const isAdminReducer = (state = { allCookies: getAll() }, action) => {
  switch (action.type) {
    case "isAdmin":
      return {
        isAdmin: state.allCookies,
      };

    default:
      return state;
  }
};

export default isAdminReducer;
