import React, { Component } from "react";
import {
  Form,
  Card,
  Input,
  Select,
  Button,
  message,
  Row,
  Col,
  InputNumber,
} from "antd";
const layoutButton = {
  labelCol: { span: 8 },
  wrapperCol: { span: 16 },
};
const layoutForm = {
  labelCol: { span: 6 },
  wrapperCol: { span: 10 },
};
const children = [];
class ModifyOpenshifts extends Component {
  constructor(props) {
    super(props);
    //const { getFieldDecorator } = this.props.form;
    console.log("ModifyInstances~~", this);
    this.state = {
      value: "",
      isShowEdit: false,
      defaultHyper: -1,
      currentData: [],
      instZone: {},
      instFlavor: {},
      instInterface: {},
      instSubnet: {},
      images: [],
      hypers: [],
      zones: [],
      flavors: [],
      keys: [],
      secgroups: [],
      subnets: [],
      zone: {
        ID: "",
        Name: "",
      },
    };
  }
  listOpenshifts = () => {
    this.props.history.push("/openshifts");
  };
  render() {
    return (
      <Card
        title={
          this.state.isShowEdit
            ? "Edit Openshift Cluster"
            : "Create New Openshift Cluster"
        }
        extra={
          <Button type="primary" onClick={this.listOpenshifts}>
            Return
          </Button>
        }
      >
        <Form
          layout="horizontal"
          onSubmit={(e) => {
            this.handleSubmit(e);
          }}
          wrapperCol={{ ...layoutForm.wrapperCol }}
        >
          <Form.Item
            label="Cluster Name"
            name="clustername"
            labelCol={{ ...layoutForm.labelCol }}
          >
            {this.props.form.getFieldDecorator("clustername", {
              rules: [
                {
                  required: true,
                },
              ],
              initialValue: this.state.currentData.clustername,
            })(
              <Input
                ref={(c) => {
                  this.hostname = c;
                }}
                disabled={this.state.isShowEdit}
              />
            )}
          </Form.Item>
          <Form.Item
            label="Base Domain"
            name="basedomain"
            labelCol={{ ...layoutForm.labelCol }}
          >
            {this.props.form.getFieldDecorator("basedomain", {
              rules: [
                {
                  required: true,
                },
              ],
              initialValue: this.state.currentData.basedomain,
            })(<Input disabled={this.state.isShowEdit} />)}
          </Form.Item>
          <Form.Item
            label="Zone"
            name="zone"
            labelCol={{ ...layoutForm.labelCol }}
            hidden={this.state.isShowEdit}
          >
            {this.props.form.getFieldDecorator("zone", {
              rules: [
                {
                  required: this.state.isShowEdit,
                },
              ],
              initialValue: this.state.currentData.Hyper,
            })(
              <Select>
                <Select.Option key={1} value={1543}>
                  zone0
                </Select.Option>
              </Select>
            )}
          </Form.Item>

          <Form.Item
            label="Infrastructure Type"
            name="infrtype"
            labelCol={{ ...layoutForm.labelCol }}
            hidden={!this.state.isShowEdit}
          >
            {this.props.form.getFieldDecorator("infrtype", {
              rules: [
                {
                  required: this.state.isShowEdit,
                },
              ],
              initialValue: this.state.currentData.infrtype,
            })(<Input placeholder="Infrastructure Type" />)}
          </Form.Item>
          <Form.Item
            label="Updated At"
            name="updatedAt"
            labelCol={{ ...layoutForm.labelCol }}
            hidden={!this.state.isShowEdit}
          >
            {this.props.form.getFieldDecorator("updatedAt", {
              rules: [],
              initialValue: this.state.currentData.UpdatedAt,
            })(<Input disabled={this.state.isShowEdit} name="updatedAt" />)}
          </Form.Item>

          <Form.Item
            label="Count"
            name="count"
            labelCol={{ ...layoutForm.labelCol }}
            // wrapperCol={{ ...layoutButton.wrapperCol }}
            hidden={this.state.isShowEdit}
          >
            {this.props.form.getFieldDecorator("count", {
              rules: [
                {
                  required: true,
                },
                // {
                //   validator: checkCount,
                // },
              ],
              initialValue: 1,
            })(
              <InputNumber
                min={1}
                name="count"
                // onChange={(e) => this.setState({ count: e.target.value })}
              />
            )}
          </Form.Item>
          <Form.Item
            name="image"
            label="Image"
            labelCol={{ ...layoutForm.labelCol }}
            hidden={this.state.isShowEdit}
          >
            {this.props.form.getFieldDecorator("image", {
              rules: [
                {
                  required: !this.state.isShowEdit,
                },
              ],
            })(
              <Select
                disabled={this.state.isShowEdit}
                // labelInValue
                // onChange={(e) => this.setState({ image: e.key })}
              >
                {this.state.images.map((val) => {
                  return (
                    <Select.Option key={val.ID} value={val.ID}>
                      {val.Name}
                    </Select.Option>
                  );
                })}
              </Select>
            )}
          </Form.Item>
          <Form.Item
            name="flavor"
            label="Flavor"
            labelCol={{ ...layoutForm.labelCol }}
          >
            {this.props.form.getFieldDecorator("flavor", {
              rules: [
                {
                  required: true,
                },
              ],
              initialValue:
                // this.state.test,
                this.state.currentData.length === 0
                  ? ""
                  : this.state.currentData.FlavorID +
                    "-" +
                    this.state.instFlavor.Name,
            })(
              <Select>
                {this.state.flavors.map((val) => {
                  return (
                    <Select.Option key={val.ID} value={val.ID}>
                      {val.Name}
                    </Select.Option>
                  );
                })}
              </Select>
            )}
          </Form.Item>
          <Form.Item
            name="primary"
            label="Primary Interface"
            labelCol={{ ...layoutForm.labelCol }}
            hidden={this.state.isShowEdit}
          >
            {this.props.form.getFieldDecorator("primary", {
              rules: [
                {
                  required: !this.state.isShowEdit,
                },
              ],
            })(
              <Select disabled={this.state.isShowEdit}>
                {this.state.subnets.map((val) => {
                  if (
