import React, { Component } from "react";
import moment from "moment";
import { Card, Table, Button, Popconfirm, Input } from "antd";
import { orgsListApi } from "../../service/orgs";
const { Search } = Input;
const columns = [
  {
    title: "ID",
    dataIndex: "ID",
    key: "ID",
    width: 80,
    align: "center",
    //render: (txt, record, index) => index + 1,
  },
  {
    title: "Name",
    dataIndex: "name",
    align: "center",
  },
  {
    title: "Created At",
    dataIndex: "CreatedAt",
    align: "center",
    render: (record) => (
      // console.log("createAt", record);
      <span>{moment(record).format("YYYY-MM-DD HH:mm:ss")}</span>
    ),
  },
  {
    title: "Action",
    align: "center",
    render: (txt, record, index) => {
      return (
        <div>
          <Button type="primary" size="small">
            Edit
          </Button>
          <Popconfirm
            title="Are you sure to delete?"
            onCancel={() => {
              console.log("cancelled");
            }}
            onConfirm={() => {
              console.log("confirmed");
              //此处调用api接口进行相关操作
            }}
          >
            <Button style={{ margin: "0 1rem" }} type="danger" size="small">
              Delete
            </Button>
          </Popconfirm>
        </div>
      );
    },
  },
];
class Orgs extends Component {
  constructor(props) {
    super(props);
    this.state = {
      orgs: [],
      isLoaded: false,
    };
  }
  componentDidMount() {
    const _this = this;
    orgsListApi()
      .then((res) => {
        console.log("componentDidMount-orgsListApi:", res);
        _this.setState({
          orgs: res.orgs,
          isLoaded: true,
          total: res.total,
        });
      })
      .catch((error) => {
        _this.setState({
          isLoaded: false,
          error: error,
        });
      });
  }
  render() {
    return (
      <Card
        title={
          "Organization Manage Panel" + "(Total: " + this.state.total + ")"
        }
        extra={
          <>
            <Search
              placeholder="Search..."
              onSearch={(value) => console.log(value)}
              enterButton
            />
            <Button
              style={{
                float: "right",
                "padding-left": "10px",
                "padding-right": "10px",
              }}
              type="primary"
              size="small"
              //onClick={() => this.props.history.push("`/orgs/${orgsid}`")}
            >
              Create
            </Button>
          </>
        }
      >
        <Table
          rowKey="ID"
          columns={columns}
          bordered
          dataSource={this.state.orgs}
        ></Table>
      </Card>
    );
  }
}
export default Orgs;
