*/
import {
  KEY_LIST_IS_FETCHING,
  KEY_LIST_FETCHED,
  KEY_LIST_FILTER,
} from "../../constants/actionTypes";

const initialState = {
  keyList: [],
  total: 0,
  filteredList: [],
  isLoading: false,
  errorMessage: "",
  keyword: "",
};
const getFilteredList = (keyList, keyword) => {
  console.log("getFilteredkeyList-keyList:", keyList);
  console.log("getFilteredkeyList-keyword:", keyword);
  return keyList.filter(
    (item) =>
      item.ID.toString().indexOf(keyword) > -1 ||
      item.name.toLowerCase().indexOf(keyword) > -1
  );
};
export default function KeyReducer(state = initialState, action) {
  console.log("initialState-state", state);
  switch (action.type) {
    case KEY_LIST_IS_FETCHING:
      return {
        ...state,
        isLoading: action.loading,
      };
    case KEY_LIST_FETCHED:
      console.log("KEY_LIST_FETCHED", action);
      return {
        ...state,
        // isLoading: action.loading,
        keyList: action.keyList,
        filteredList: getFilteredList(action.keyList, state.keyword),
      };
    case KEY_LIST_FILTER:
      console.log("KEY_LIST_FILTER-state", state);
      return {
        ...state,
        keyword: action.keyword,
        filteredList: getFilteredList(state.keyList, action.keyword),
      };
    default:
      return state;
  }
}
