import { USER_LIST_FETCHED, LIST_FILTER } from "../../constants/actionTypes";
import { regListApi, delRegInfor } from "../../service/registrys";
export const filterRegList = (keyword) => ({
  type: LIST_FILTER,
  keyword,
});
export const fetchRegList = () => {
  return (dispatch) => {
    //   dispatch(fetchingUserList(true));

    regListApi().then((res) => {
      console.log("regAction-res", res);
      // let resData = res.data;
      if (res && res.registrys.length !== 0) {
        // dispatch(fetchingUserList(false));
        dispatch(fetchUserListSuccess(res.registrys));
      } else {
        //   dispatch(fetchingUserList(false));
        //   dispatch(fetchUserListFailed(" 获取用户列表失败"));
      }
    });
    //   .catch((e) => dispatch(fetchUserListFailed(e.message)));
  };
};
export const fetchUserListSuccess = (userList) => ({
  type: USER_LIST_FETCHED,
  userList,
});
