import {
  FLOATINGIP_LIST_IS_FETCHING,
  FLOATINGIP_LIST_FETCHED,
  FLOATINGIP_LIST_FILTER,
} from "../../constants/actionTypes";

const initialState = {
  floatList: [],
  total: 0,
  filteredList: [],
  isLoading: false,
  errorMessage: "",
  keyword: "",
};
const getFilteredList = (floatList, keyword) => {
  console.log("getFilteredUserList-floatList:", floatList);
  console.log("getFilteredUserList-keyword:", keyword);
  return floatList.filter((item) => item.ID.toString().indexOf(keyword) > -1);
};
export default function FloatingIPReducer(state = initialState, action) {
  console.log("initialState-state", state);
  switch (action.type) {
    case FLOATINGIP_LIST_IS_FETCHING:
      return {
        ...state,
        isLoading: action.loading,
      };
    case FLOATINGIP_LIST_FETCHED:
      console.log("FLOATINGIP_LIST_FETCHED", action);
      return {
        ...state,
        // isLoading: action.loading,
        floatList: action.floatList,
        filteredList: getFilteredList(action.floatList, state.keyword),
      };
    case FLOATINGIP_LIST_FILTER:
      console.log("LIST_FILTER-state", state);
      return {
        ...state,
        keyword: action.keyword,
        filteredList: getFilteredList(state.floatList, action.keyword),
      };
    default:
      return state;
  }
}
