import React, { Component } from "react";
import {
  Form,
  Card,
  Input,
  Select,
  Button,
  message,
  Row,
  Col,
  InputNumber,
} from "antd";
import {
  editOcpInfor,
  createOcpApi,
  getOcpInforById,
} from "../../service/openshifts";
import { flavorsListApi } from "../../service/flavors";
import { subnetsListApi } from "../../service/subnets";
import { keysListApi } from "../../service/keys";
const layoutButton = {
  labelCol: { span: 8 },
  wrapperCol: { span: 16 },
};
const layoutForm = {
  labelCol: { span: 6 },
  wrapperCol: { span: 10 },
};
class ModifyOpenshifts extends Component {
  constructor(props) {
    super(props);
    //const { getFieldDecorator } = this.props.form;
    console.log("ModifyOcp~~", this);
    this.state = {
      value: "",
      isShowEdit: false,
      openshifts: [],
      currentData: [],
      registrys: [],
      flavors: [],
      keys: [],
      subnets: [],
      zones: [],

      zone: {
        ID: "",
        Name: "",
      },
    };
    let that = this;
    if (props.match.params.id) {
      getOcpInforById(props.match.params.id).then((res) => {
        console.log("getOcpInforById-res:", res);
        that.setState({
          currentData: res,
          isShowEdit: true,
        });

        console.log("getOcpInforById~state:", this.state);
      });
    }
  }
  componentWillMount() {
    const _this = this;

    flavorsListApi()
      .then((res) => {
        _this.setState({
          flavors: res.flavors,
          isLoaded: true,
        });
        console.log("flavors:", res);
      })
      .catch((error) => {
        _this.setState({
          isLoaded: false,
          error: error,
        });
      });
    subnetsListApi()
      .then((res) => {
        _this.setState({
          subnets: res.subnets,
          isLoaded: true,
        });
      })
      .catch((error) => {
        _this.setState({
          isLoaded: false,
          error: error,
        });
      });

    keysListApi()
      .then((res) => {
        console.log("componentWillMount-keys:", res);
        _this.setState({
          keys: res.keys,
          isLoaded: true,
        });
      })
      .catch((error) => {
        _this.setState({
          isLoaded: false,
          error: error,
        });
      });
  }
  listOpenshifts = () => {
    this.props.history.push("/openshifts");
  };
  handleSubmit = (e) => {
    console.log("handleSubmit:", e);
    let tempOwner = {};
    e.preventDefault();
    this.props.form.validateFieldsAndScroll((err, values) => {
      if (!err) {
        console.log("handleSubmit-value-ocp:", values);
        console.log("提交");
        if (this.props.match.params.id) {
          console.log("ocp-edit", this.props.match.params.id, values);
          editOcpInfor(this.props.match.params.id, values).then((res) => {
            console.log("ocp-editInsInfor:", res);
            this.props.history.push("/openshifts");
          });
        } else {
          tempOwner = this.state.secgroups.map((item) => {
            if (item.Name === window.localStorage.token) {
              console.log("item-secgroups", item);
              return item.ID;
            }
          });
          console.log("tempOwner", tempOwner);
          values.hyper =
            values.hyper === undefined ? this.state.defaultHyper : values.hyper;

          values.secgroups =
            values.secgroups === undefined
              ? `${tempOwner}`
              : `${values.secgroups}`;

          values.keys = `${values.keys}`;
          console.log("submit-value", values);
          createOcpApi(values)
            .then((res) => {
              console.log("handleSubmit-res-createInsApi:", res);
              this.props.history.push("/instances");
              // Utils.loadData(this.state.current, this.state.pageSize)
            })
            .catch((err) => {
              console.log("handleSubmit-error:", err);
            });
        }
      } else {
        message.error(" input wrong information");
      }
    });
  };
  render() {
    return (
      <Card
        title={
          this.state.isShowEdit
            ? "Edit Openshift Cluster"
            : "Create New Openshift Cluster"
        }
        extra={
          <Button type="primary" onClick={this.listOpenshifts}>
            Return
          </Button>
        }
      >
        <Form
          layout="horizontal"
          onSubmit={(e) => {
            this.handleSubmit(e);
          }}
          wrapperCol={{ ...layoutForm.wrapperCol }}
        >
          <Form.Item
            label="Cluster Name"
            name="clustername"
            labelCol={{ ...layoutForm.labelCol }}
          >
            {this.props.form.getFieldDecorator("clustername", {
              rules: [
                {
                  required: true,
                },
              ],
              initialValue: this.state.currentData.ClusterName,
            })(
              <Input
                ref={(c) => {
                  this.hostname = c;
                }}
                disabled={this.state.isShowEdit}
              />
            )}
          </Form.Item>
          <Form.Item
            label="Base Domain"
            name="basedomain"
            labelCol={{ ...layoutForm.labelCol }}
          >
            {this.props.form.getFieldDecorator("basedomain", {
              rules: [
                {
                  required: true,
                },
              ],
              initialValue: this.state.currentData.BaseDomain,
            })(<Input disabled={this.state.isShowEdit} />)}
          </Form.Item>
          <Form.Item
            label="Created At"
            name="createdAt"
            labelCol={{ ...layoutForm.labelCol }}
            hidden={!this.state.isShowEdit}
          >
