import { LIST_FILTER } from "../../constants/actionTypes";

const initialState = {
  regList: [],
  filteredUserList: [],
  isLoading: false,
  errorMessage: "",
  keyword: "",
};
const getFilteredList = (regList, keyword) => {
  console.log("getFilteredUserList-userList:", regList);
  console.log("getFilteredUserList-keyword:", keyword);
  return regList.filter(
    (user) => user.uid.indexOf(keyword) > -1 || user.mail.indexOf(keyword) > -1
  );
};
export default function reg(state = initialState, action) {
  switch (action.type) {
    case LIST_FILTER:
      console.log("LIST_FILTER-state", state);
      return {
        ...state,
        keyword: action.keyword,
        filteredList: getFilteredList(state.regList, action.keyword),
      };
    default:
      return state;
  }
}
