import React, { Component } from "react";
import { Card, Table, Button, Popconfirm } from "antd";
import { ocpListApi } from "../../service/openshifts";
const columns = [
  {
    title: "ID",
    dataIndex: "ID",
    key: "ID",
    width: 80,
    align: "center",
  },
  {
    title: "Cluster Name",
    dataIndex: "ClusterName",
    align: "center",
  },
  {
    title: "Base Domain",
    dataIndex: "BaseDomain",
    align: "center",
  },
  {
    title: "Console",
    dataIndex: "console",
    align: "center",
  },
  {
    title: "Version",
    dataIndex: "Version",
    align: "center",
  },
  {
    title: "Flavor",
    dataIndex: "Flavor",
    align: "center",
  },
  {
    title: "HA",
    dataIndex: "Haflag",
    // width: 50,
    align: "center",
  },
  {
    title: "N-Workers",
    dataIndex: "WorkerNum",
    align: "center",
  },
  {
    title: "Status",
    dataIndex: "Status",
    align: "center",
  },
  {
    title: "Action",
    align: "center",
    render: (txt, record, index) => {
      return (
        <div>
          <Button
            style={{
              marginTop: "10px",
            }}
            type="primary"
            size="small"
          >
            Edit
          </Button>
          <Popconfirm
            title="Are you sure to delete?"
            onCancel={() => {
              console.log("cancelled");
            }}
            onConfirm={() => {
              console.log("confirmed");
              //此处调用api接口进行相关操作
            }}
          >
            <Button
              style={{
                margin: "5px",
                marginRight: "0px",
                marginTop: "10px",
              }}
              type="danger"
              size="small"
            >
              Delete
            </Button>
          </Popconfirm>
        </div>
      );
    },
  },
];
class Openshifts extends Component {
  constructor(props) {
    super(props);
    console.log("Openshifts.props:", this.props);
    this.state = {
      openshifts: [],
      isLoaded: false,
      total: 0,
    };
  }
  //组件初始化的时候执行
  componentWillMount() {
    const _this = this;
    console.log("componentDidMount:", this);
    ocpListApi()
      .then((res) => {
        _this.setState({
          openshifts: res.openshifts,
          isLoaded: true,
          total: res.total,
        });
        console.log(res);
      })
      .catch((error) => {
        _this.setState({
          isLoaded: false,
          error: error,
        });
      });
  }
  render() {
    return (
      <Card
        title={"Openshift Manage Panel" + "(Total: " + this.state.total + ")"}
        extra={
          <Button type="primary" size="small">
            Create
          </Button>
        }
      >
        <Table
          rowKey="ID"
          columns={columns}
          bordered
          dataSource={this.state.openshifts}
        ></Table>
      </Card>
    );
  }
}
export default Openshifts;
