import { REG_LIST_FETCHED, LIST_FILTER } from "../../constants/actionTypes";

const initialState = {
  regList: [],
  filteredList: [],
  isLoading: false,
  errorMessage: "",
  keyword: "",
};
const getFilteredList = (regList, keyword) => {
  console.log("getFilteredUserList-regList:", regList);
  console.log("getFilteredUserList-keyword:", keyword);
  return regList.filter(
    (item) => {
      console.log("reglist-item", item);
    }

    // (user) => user.id.indexOf(keyword) > -1 || user.mail.indexOf(keyword) > -1
  );
};
export default function RegReducer(state = initialState, action) {
  switch (action.type) {
    case REG_LIST_FETCHED:
      return {
        ...state,
        userList: action.userList,
        filteredUserList: getFilteredUserList(action.userList, state.keyword),
      };
    case LIST_FILTER:
      console.log("LIST_FILTER-state", state);
      return {
        ...state,
        keyword: action.keyword,
        filteredList: getFilteredList(state.regList, action.keyword),
      };
    default:
      return state;
  }
}
