import React, { Component } from "react";
import { Input, Select, Form, Modal } from "antd";
import { hypersListApi } from "../../service/hypers";
import { flavorsListApi } from "../../service/flavors";
const layoutForm = {
  labelCol: { span: 8 },
  wrapperCol: { span: 10 },
  layouttype: "horizontal",
};
const modalFormItem = {
  labelCol: {
    xs: { span: 24 },
    sm: { span: 6 },
  },
  wrapperCol: {
    xs: { span: 24 },
    sm: { span: 16 },
  },
};
const { Option } = Select;
class CreateKeyModal extends Component {
  constructor(props) {
    super(props);
    this.state = {
      flavors: [],
      hypers: [],
      status: [],
      isLoaded: false,
    };
  }

  handleOk = () => {
    const p = this;
    const { form } = this.props;
    console.log("handleOk-key-form", this.props);
    form.validateFieldsAndScroll((err, values) => {
      console.log("handleOk-key", values);
      if (err) {
        return;
      }
      p.props.submit(values);
    });
  };

  handleCancel = () => {
    const { close } = this.props;
    close();
  };

  render() {
    console.log("instModal-key", this.props.key);
    // const { getFieldDecorator } = this.props.form;
    return (
      <div>
        <Modal
          destroyOnClose
          title={this.props.title}
          visible={this.props.visible}
          onOk={this.handleOk}
          onCancel={this.handleCancel}
          maskClosable={false}
        >
          <Form>
            <Form.Item label="Name" name="name" {...modalFormItem}>
              {this.props.form.getFieldDecorator("name", {
                rules: [
                  {
                    required: true,
                  },
                ],
              })(<Input />)}
            </Form.Item>
            <Form.Item name="pubkey" label="Public Key" {...modalFormItem}>
              {this.props.form.getFieldDecorator("pubkey", {
                rules: [
                  {
                    required: true,
                  },
                ],
              })(<Input.TextArea />)}
            </Form.Item>
          </Form>
        </Modal>
      </div>
    );
  }
}

export default Form.create({ name: "createKeyModal" })(CreateKeyModal);
