import {
  INST_LIST_IS_FETCHING,
  INS_LIST_FETCHED,
  INS_LIST_FILTER,
} from "../../constants/actionTypes";

const initialState = {
  insList: [],
  total: 0,
  filteredList: [],
  isLoading: false,
  errorMessage: "",
  keyword: "",
};
const getFilteredList = (insList, keyword) => {
  console.log("getFilteredUserList-regList:", insList);
  console.log("getFilteredUserList-keyword:", keyword);
  return insList.filter((item) => item.ID.toString().indexOf(keyword) > -1);
};
export default function InstReducer(state = initialState, action) {
  console.log("initialState-state", state);
  switch (action.type) {
    case INS_LIST_IS_FETCHING:
      return {
        ...state,
        isLoading: action.loading,
      };
    case INS_LIST_FETCHED:
      console.log("INS_LIST_FETCHED", action);
      return {
        ...state,
        // isLoading: action.loading,
        insList: action.regList,
        filteredList: getFilteredList(action.insList, state.keyword),
      };
    case INS_LIST_FILTER:
      console.log("LIST_FILTER-state", state);
      return {
        ...state,
        keyword: action.keyword,
        filteredList: getFilteredList(state.insList, action.keyword),
      };
    default:
      return state;
  }
}
