import React, { Component } from "react";
import {
  Form,
  Card,
  Input,
  Select,
  Button,
  message,
  Row,
  Col,
  InputNumber,
} from "antd";
const layoutButton = {
  labelCol: { span: 8 },
  wrapperCol: { span: 16 },
};
const layoutForm = {
  labelCol: { span: 6 },
  wrapperCol: { span: 10 },
};
class ModifyOpenshifts extends Component {
  constructor(props) {
    super(props);
    //const { getFieldDecorator } = this.props.form;
    console.log("ModifyInstances~~", this);
    this.state = {
      value: "",
      isShowEdit: false,
      defaultHyper: -1,
      currentData: [],
      registrys: [],
      instZone: {},
      instFlavor: {},
      instInterface: {},
      instSubnet: {},
      images: [],
      hypers: [],
      zones: [],
      flavors: [],
      keys: [],
      subnets: [],
      zone: {
        ID: "",
        Name: "",
      },
    };
  }
  listOpenshifts = () => {
    this.props.history.push("/openshifts");
  };
  render() {
    return (
      <Card
        title={
          this.state.isShowEdit
            ? "Edit Openshift Cluster"
            : "Create New Openshift Cluster"
        }
        extra={
          <Button type="primary" onClick={this.listOpenshifts}>
            Return
          </Button>
        }
      >
        <Form
          layout="horizontal"
          onSubmit={(e) => {
            this.handleSubmit(e);
          }}
          wrapperCol={{ ...layoutForm.wrapperCol }}
        >
          <Form.Item
            label="Cluster Name"
            name="clustername"
            labelCol={{ ...layoutForm.labelCol }}
          >
            {this.props.form.getFieldDecorator("clustername", {
              rules: [
                {
                  required: true,
                },
              ],
              initialValue: this.state.currentData.clustername,
            })(
              <Input
                ref={(c) => {
                  this.hostname = c;
                }}
                disabled={this.state.isShowEdit}
              />
            )}
          </Form.Item>
          <Form.Item
            label="Base Domain"
            name="basedomain"
            labelCol={{ ...layoutForm.labelCol }}
          >
            {this.props.form.getFieldDecorator("basedomain", {
              rules: [
                {
                  required: true,
                },
              ],
              initialValue: this.state.currentData.basedomain,
            })(<Input disabled={this.state.isShowEdit} />)}
          </Form.Item>
          <Form.Item
            label="Zone"
            name="zone"
            labelCol={{ ...layoutForm.labelCol }}
            hidden={this.state.isShowEdit}
          >
            {this.props.form.getFieldDecorator("zone", {
              rules: [
                {
                  required: !this.state.isShowEdit,
                },
              ],
              initialValue: this.state.currentData.Hyper,
            })(
              <Select>
                <Select.Option key={1} value={1543}>
                  zone0
                </Select.Option>
              </Select>
            )}
          </Form.Item>

          <Form.Item
            label="Infrastructure Type"
            name="infrtype"
            labelCol={{ ...layoutForm.labelCol }}
            hidden={this.state.isShowEdit}
          >
            {this.props.form.getFieldDecorator("infrtype", {
              rules: [
                {
                  required: !this.state.isShowEdit,
                },
              ],
              // initialValue: this.state.currentData.infrtype,
            })(
              <Select>
                <Select.Option key="s390x" value="s390x">
                  z/VM
                </Select.Option>
                <Select.Option key="x86-64" value="kvm-x86_64">
                  KVM on x84_64
                </Select.Option>
                <Select.Option key="2" value="kvm-s390x">
                  KVM on Z
                </Select.Option>
              </Select>
            )}
          </Form.Item>
          <Form.Item
            label="High Available"
            name="haflag"
            labelCol={{ ...layoutForm.labelCol }}
            hidden={this.state.isShowEdit}
          >
            {this.props.form.getFieldDecorator("haflag", {
              rules: [],
              // initialValue: "no",
            })(
              <Select placeholder="no">
                <Select.Option key="yes" value="yes">
                  yes
                </Select.Option>
                <Select.Option key="no" value="no">
                  no
                </Select.Option>
              </Select>
            )}
          </Form.Item>

          <Form.Item
            label="Number of Workers"
            name="nworkers"
            labelCol={{ ...layoutForm.labelCol }}
          >
            {this.props.form.getFieldDecorator("nworkers", {
              rules: [],
              initialValue: 2,
            })(<InputNumber min={2} name="nworkers" />)}
          </Form.Item>
          <Form.Item
            label="Registry"
            name="registry"
            labelCol={{ ...layoutForm.labelCol }}
            hidden={this.state.isShowEdit}
          >
            {this.props.form.getFieldDecorator("registry", {
              rules: [
                {
                  required: !this.state.isShowEdit,
                },
              ],
            })(
              <Select>
                {this.state.registrys.map((val) => {
                  return (
                    <Select.Option key={val.ID} value={val.ID}>
                      {val.Name}
                    </Select.Option>
                  );
                })}
              </Select>
            )}
          </Form.Item>
          <Form.Item
            label="LB_Flavor"
            name="lflavor"
            labelCol={{ ...layoutForm.labelCol }}
          >
            {this.props.form.getFieldDecorator("lflavor", {
              rules: [
                {
                  required: true,
                },
              ],
              initialValue:
                // this.state.test,
                this.state.currentData.length === 0
                  ? ""
                  : this.state.currentData.FlavorID +
                    "-" +
                    this.state.instFlavor.Name,
            })(
              <Select>
                {this.state.flavors.map((val) => {
                  return (
                    <Select.Option key={val.ID} value={val.ID}>
                      {val.Name}
                    </Select.Option>
                  );
                })}
              </Select>
            )}
          </Form.Item>
          <Form.Item
            label="Master_Flavor"
            name="mflavor"
            labelCol={{ ...layoutForm.labelCol }}
          >
            {this.props.form.getFieldDecorator("mflavor", {
              rules: [
                {
                  required: true,
                },
              ],
              initialValue:
                // this.state.test,
                this.state.currentData.length === 0
                  ? ""
                  : this.state.currentData.FlavorID +
                    "-" +
                    this.state.instFlavor.Name,
            })(
              <Select>
                {this.state.flavors.map((val) => {
                  return (
                    <Select.Option key={val.ID} value={val.ID}>
                      {val.Name}
                    </Select.Option>
                  );
                })}
              </Select>
            )}
          </Form.Item>
          <Form.Item
            label="Worker_Flavor"
            name="wflavor"
            labelCol={{ ...layoutForm.labelCol }}
          >
            {this.props.form.getFieldDecorator("wflavor", {
              rules: [
                {
                  required: true,
                },
              ],
              initialValue:
                // this.state.test,
                this.state.currentData.length === 0
                  ? ""
                  : this.state.currentData.FlavorID +
                    "-" +
                    this.state.instFlavor.Name,
            })(
              <Select>
                {this.state.flavors.map((val) => {
                  return (
                    <Select.Option key={val.ID} value={val.ID}>
                      {val.Name}
                    </Select.Option>
                  );
                })}
              </Select>
            )}
          </Form.Item>

          <Form.Item
            label="Subnet"
            name="sunbet"
            labelCol={{ ...layoutForm.labelCol }}
            hidden={this.state.isShowEdit}
          >
            {this.props.form.getFieldDecorator("sunbet", {
              rules: [],
            })(
              <Select placeholder="Subnet">
                <Select.Option key="public" value="public">
                  public
                </Select.Option>
              </Select>
            )}
          </Form.Item>

          <Form.Item
            label="Key"
            name="key"
            labelCol={{ ...layoutForm.labelCol }}
            // hidden={this.state.isShowEdit}
          >
            <Form.Item>
              {this.props.form.getFieldDecorator("key", {
                rules: [
                  {
                    required: true,
                  },
                ],
              })(
                <Select placeholder="Key">
                  {this.state.keys.map((val, index) => {
                    return (
                      <Select.Option key={index} value={val.ID}>
                        {val.ID} - {val.Name}
                      </Select.Option>
                    );
                  })}
                </Select>
              )}
            </Form.Item>
          </Form.Item>

          <Form.Item
            wrapperCol={{ ...layoutButton.wrapperCol, offset: 8 }}
            labelCol={{ span: 6 }}
          >
            {this.state.isShowEdit ? (
              <Button type="primary" htmlType="submit">
                Update Openshift Cluster
              </Button>
            ) : (
              <Button type="primary" htmlType="submit">
                Create New Openshift Cluster
              </Button>
            )}
          </Form.Item>
        </Form>
      </Card>
    );
  }
}
export default Form.create({ name: "modifyOpenshifts" })(ModifyOpenshifts);
