import {
  SECRULE_LIST_IS_FETCHING,
  SECRULE_LIST_FETCHED,
  SECRULE_LIST_FILTER,
} from "../../constants/actionTypes";

const initialState = {
  secruleList: [],
  total: 0,
  filteredList: [],
  isLoading: false,
  errorMessage: "",
  keyword: "",
};
const getFilteredList = (secruleList, keyword) => {
  console.log("getFilteredUserList-secruleList:", secruleList);
  console.log("getFilteredUserList-keyword:", keyword);
  return secruleList.filter((item) => item.ID.toString().indexOf(keyword) > -1);
};
export default function RegReducer(state = initialState, action) {
  console.log("initialState-state", state);
  switch (action.type) {
    case SECRULE_LIST_IS_FETCHING:
      return {
        ...state,
        isLoading: action.loading,
      };
    case SECRULE_LIST_FETCHED:
      console.log("SECRULE_LIST_FETCHED", action);
      return {
        ...state,
        secruleList: action.secruleList,
        filteredList: getFilteredList(action.secruleList, state.keyword),
      };
    case SECRULE_LIST_FILTER:
      console.log("SECRULE_LIST_FILTER-state", state);
      return {
        ...state,
        keyword: action.keyword,
        filteredList: getFilteredList(state.secruleList, action.keyword),
      };
    default:
      return state;
  }
}
